package Java_addon;

public class switch1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day=2;
		
		switch (day) {
		case 1:
			System.out.println("monday");
			break;
		case 2:
			System.out.println("tuesday");
			break;
		case 3:	
			System.out.println("saturday");
			break;
		default:
			System.out.println("sunday");
			break;
		}

	}

}
